mapper.select:
	select 

  from product
  where is_deleted=1

  	and product_name = #{productName}


  	and product_title = #{productTitle}

  limit #{startRow},#{pageSize};

	select 
  	count(*)
  from product
  where is_deleted=1

  	and product_name = #{productName}


  	and product_title = #{productTitle}
